mapper.update:
UPDATE TB_COMMENT
SET
    COMMENT_CONTENT = #{commentContent}
WHERE
    COMMENT_NO = #{commentNo}
    AND COMMENT_STATUS = 'Y'

UPDATE TB_COMMENT
SET COMMENT_STATUS = 'N'
WHERE COMMENT_NO = #{commentNo}

mapper.select:
SELECT COMMENT_WRITER
FROM TB_COMMENT
WHERE COMMENT_NO = #{commentNo}
